The following example demonstrates the implementation of Extract. 

public object Extract(object value)
{
   try
   {
      if (value.GetType() == typeof(int))
       {
           return 0;
       }
      if (value.GetType() == typeof(float))
       {
           return 0.0;
       }
   }
   catch (Exception e)
   {
      //handle exception
   }
   return value;
}

